#ysw_fa_wrapper {
    width: 100%;
    background-color: #e5f0fc;
    background-image: url(https://cdn11.bigcommerce.com/s-snboj/stencil/023dae90-db09-0139-3418-46e39c32b38c/e/c9f4a7d0-d8d9-0139-1e5b-3a208d481fb5/img/home-app-bg-5.jpg);
    background-size: cover;
    background-position: left top;
    font-family: 'RobotoG',Arial,Helvetica,sans-serif;
    padding: 40px 10px;
    display: inline-block;
	//margin-top:20px;

	.ysw_fa_start_view {

		.ysw_fa_heading {
			color:#000000;
			font-size:36px;
			text-align:center;
			text-transform:uppercase;
			line-height:1;

			span {
				font-weight:600;
				color:#0266C8;
			}
		}

		.ysw_fa_steps_container {
			width:100%;
			max-width:1100px;
			margin:0 auto;
			padding:10px 0;

			&:after {
				content:'';
				display:block;
				clear:both;
			}
			.step_disabled {
				opacity:0.6;
				-webkit-user-select: none; /* Safari 3.1+ */
				-moz-user-select: none; /* Firefox 2+ */
				-ms-user-select: none; /* IE 10+ */
				user-select: none; /* Standard syntax */
			}
			.step {
				float:left;

				.step-title {
					color:#0266C8;
					text-transform: uppercase;
					font-size:20px;
					line-height:1;
					padding-bottom:10px;
				}

				select {
				width: 100%;
                height: 40px;
                background-color: #fff;
                border: 1px solid #ccc;
                padding: 0 10px;
                font-size: 12pt;
                /* text-transform: uppercase; */
                border-radius: 3px;
				}
                select:disabled
                {
                    color:#ccc;
                }
				&.ysw_filter_step_1 {
					
				}

				&.ysw_filter_step_2 {
					margin:0;
				}

				&.ysw_filter_step_3 {

					.year-help {
                        text-align: left;
                        padding-top: 5px;
                        line-height: 1;
                        font-size: 8pt;
						a{
						    color:#0066cb;
						}
					}
					
				}
			}
		}

		.ysw_fa_submit_container {
		    margin: 0 20px 10px 20px;
			text-align:left;
			button {
				background-color:#89980f;
				color:#ffffff;
				font-weight: 600;
				font-size: 12pt;
				line-height: 1;
				padding: 15px 75px;
				border: 0;
				&:hover {
					background-color:#707c0a;
				}
				&:disabled
                {
                    background-color:#ececec;
                }
			}
		}

		

	}

	.ysw_fa_locked_view {
		text-align:left;
        padding:7px 20px;
		.ysw_fa_selection {
			display:inline-block;
			color: #fff;
			font-size: 12pt;
			line-height:1;

			span {
				color:#fff;
				font-weight: bold;
			}
		}

		.ysw_fa_options {
			display:inline-block;
			padding-left: 20px;
    		font-size: 10pt;
    		line-height:1;
    		a{
    		   color: #ff0;
    		}
		}
	}

	.ysw_fa_locked_view,
	.ysw_fa_start_view {
		display:none;
	}

	.ysw_fa_error_message {
		text-align:center;
		display:none;

		&.show {
			display:block;
		}

		.ysw_fa_message {
			color:#bc191f;
			font-size:26px;
			line-height:1;
			padding-bottom:20px;
		}

		ul.ysw_fa_options {
			font-size:16px;
			list-style-type:none;
			padding:0;
			margin:0;

			li {

				&:nth-child(1) {
					padding-bottom:10px;
				}
			}
		}

	}

	&.locked {
    color: #fff!important;
    font-size: 9pt;
    text-align: center;
    text-transform: none;
    height: 52px;
    color:#fff;
    background-color: #0066cb !important;
		.ysw_fa_locked_view {
			display:block;
		}

	}

	&.start {

		.ysw_fa_start_view {
			display:block;
		}

	}

	&.slim-view {
		padding:30px 0;
		margin:0 0 20px;

		&:after {
			content:'';
			clear:both;
			display:block;
		}

		.ysw_fa_start_view {

			.ysw_fa_heading {
				padding-bottom:30px;
			}

			.ysw_fa_steps_container {
				width: 100%;
				float: left;
				margin: 0;
				padding: 0;
				padding: 20px;
			}
			.ysw_fa_submit_container {
				width:50%;
				float:left;
				padding:30px 20px 0;
				

				button {
				background-color:#89980f;
				color:#ffffff;
				font-weight: 600;
				font-size: 12pt;
				line-height: 1;
				padding: 15px 75px;
				border: 0;
				&:hover {
					background-color:#707c0a;
				}
				&:disabled
                {
                    background-color:#ececec;
                }

					span {
						display:none;
					}
				}
			}

		}

	}
	
}
