.homefiltersection #ysw_fa_wrapper{
    width:400px;
}
#ysw_fa_wrapper {
	padding: 0;
	margin: 0;
	background-color: #fff;
	background-image: none;
	border-top: none !important;

	.ysw_fa_start_view .ysw_fa_steps_container .step {
		width: 100%;
		float: left;
		padding: 0 10px;

		.step-title {
			color: #666;
			text-transform: none;
			font-size: 11pt;
			line-height: 1;
			padding-bottom: 10px;
			text-align: center;
		}
	}

	.ysw_fa_start_view .ysw_fa_steps_container .ysw_filter_step_1.step {
		div {
			text-align: center;

			#ysw_club_car {
				background-image: url(https://cdn11.bigcommerce.com/s-snboj/stencil/023dae90-db09-0139-3418-46e39c32b38c/e/c9f4a7d0-d8d9-0139-1e5b-3a208d481fb5/img/clubcartblue-logo.png);
				background-image: url(https://cdn11.bigcommerce.com/s-snboj/stencil/023dae90-db09-0139-3418-46e39c32b38c/e/c9f4a7d0-d8d9-0139-1e5b-3a208d481fb5/img/clubcartblue-logo.svg);
				height: 32px;
				width: 92px;
				Background-size: 100% auto;
				background-position: top center;
				background-repeat: no-repeat;
				display: inline-block;
				margin: 10px 7px;
				cursor: pointer;
				max-width: calc(33% - 14px);
			}

			#ysw_club_car:hover,
			#ysw_ezgo:hover,
			#ysw_yamaha:hover {
				background-color: #b1d2f3;
			}

			#ysw_ezgo {
				background-image: url(https://cdn11.bigcommerce.com/s-snboj/stencil/023dae90-db09-0139-3418-46e39c32b38c/e/c9f4a7d0-d8d9-0139-1e5b-3a208d481fb5/img/ezgoblue-logo.png);
				background-image: url(https://cdn11.bigcommerce.com/s-snboj/stencil/023dae90-db09-0139-3418-46e39c32b38c/e/c9f4a7d0-d8d9-0139-1e5b-3a208d481fb5/img/ezgoblue-logo.svg);
				height: 31px;
				width: 108px;
				Background-size: 100% auto;
				background-position: top center;
				background-repeat: no-repeat;
				display: inline-block;
				margin: 10px 7px;
				cursor: pointer;
				max-width: calc(33% - 14px);
			}

			#ysw_yamaha {
				background-image: url(https://cdn11.bigcommerce.com/s-snboj/stencil/023dae90-db09-0139-3418-46e39c32b38c/e/c9f4a7d0-d8d9-0139-1e5b-3a208d481fb5/img/yamahablue-logo.png);
				background-image: url(https://cdn11.bigcommerce.com/s-snboj/stencil/023dae90-db09-0139-3418-46e39c32b38c/e/c9f4a7d0-d8d9-0139-1e5b-3a208d481fb5/img/yamahablue-logo.svg);
				height: 30px;
				width: 120px;
				Background-size: 100% auto;
				display: inline-block;
				background-position: top center;
				background-repeat: no-repeat;
				margin: 10px 7px;
				cursor: pointer;
				max-width: calc(33% - 14px);
			}

			.small-logos {
				display: flex;
				width: 100%;
				align-items: center;
				justify-content: center;

				#ysw_harley_davidson {
					background-image: url(https://cdn11.bigcommerce.com/s-snboj/stencil/023dae90-db09-0139-3418-46e39c32b38c/e/c9f4a7d0-d8d9-0139-1e5b-3a208d481fb5/img/harley-davids-logo.png);
					height: 45px;
					width: 58px;
					Background-size: 58px auto;
					display: block;
					background-position: center center;
					background-repeat: no-repeat;
					margin: 10px 7px 0px 7px;
					cursor: pointer;
				}

				#ysw_taylor-dunn {
					background-image: url(https://cdn11.bigcommerce.com/s-snboj/stencil/023dae90-db09-0139-3418-46e39c32b38c/e/c9f4a7d0-d8d9-0139-1e5b-3a208d481fb5/img/taylor-dunn-logo.png);
					height: 45px;
					width: 58px;
					Background-size: 58px auto;
					display: block;
					background-position: center center;
					background-repeat: no-repeat;
					margin: 10px 7px 0px 7px;
					cursor: pointer;
				}

				#ysw_star_evn {
					background-image: url(https://cdn11.bigcommerce.com/s-snboj/stencil/023dae90-db09-0139-3418-46e39c32b38c/e/c9f4a7d0-d8d9-0139-1e5b-3a208d481fb5/img/star-ev-logo.png);
					height: 45px;
					width: 58px;
					Background-size: 58px auto;
					display: block;
					background-position: center center;
					background-repeat: no-repeat;
					margin: 10px 7px 0px 7px;
					cursor: pointer;
				}

				#ysw_columbia_par_car {
					background-image: url(https://cdn11.bigcommerce.com/s-snboj/stencil/023dae90-db09-0139-3418-46e39c32b38c/e/c9f4a7d0-d8d9-0139-1e5b-3a208d481fb5/img/columbia-logo.png);
					height: 45px;
					width: 58px;
					Background-size: 58px auto;
					display: block;
					background-position: center center;
					background-repeat: no-repeat;
					margin: 10px 7px 0px 7px;
					cursor: pointer;
				}

				#ysw_cushman {
					background-image: url(https://cdn11.bigcommerce.com/s-snboj/stencil/023dae90-db09-0139-3418-46e39c32b38c/e/c9f4a7d0-d8d9-0139-1e5b-3a208d481fb5/img/cushman-logo.png);
					height: 45px;
					width: 58px;
					Background-size: 58px auto;
					display: block;
					background-position: center center;
					background-repeat: no-repeat;
					margin: 10px 7px 0px 7px;
					cursor: pointer;
				}
			}

			#ysw_other, #ysw_dont_know {
				font-size: 12px;
				color: #666;
				display: inline-block;
				padding: 0px;
				margin: 10px 0;
				text-decoration: underline;
				cursor: pointer;
			}

			#ysw_other:hover,
			#ysw_dont_know:hover {
				color: #0066ca;
			}
		}
	}

	.ysw_fa_start_view .ysw_fa_steps_container .ysw_filter_step_1.step {
	}

	.ysw_filter_step_1.step {
		display: block
	}

	.ysw_filter_step_1.step.ysw_filter_step_1-mob {
		display: none
	}

	.ysw_fa_start_view .ysw_fa_steps_container .ysw_filter_step_1.step select {
		width: 100%;
		height: 40px;
		background-color: #fff;
		border-radius: 3px;
		border: 1px solid #cccccc;
		padding: 0 10px;
		font-size: 12pt;
		/* text-transform: uppercase; */
		option {
			color: transparent;
			font-size: 0px;
			text-transform: none;
			max-height: 36px;
			display: inline-block;
			margin: 10px 5px;
			text-align: center;
		}
	}

	#ysw_filter_make option:first-child {
		display: none;
	}

	.ysw_fa_start_view .ysw_fa_heading {
		color: #fff !important;
		font-size: 17pt;
		text-align: center;
		text-transform: none;
		line-height: 52px;
		height: 52px;
		background-color: #0066cb;

		i {
			background-color: #89980f;
			width: 40px;
			height: 40px;
			line-height: 40px;
			border-radius: 50%;
			margin-right: 10px;
		}
	}
}
#ysw_fa_wrapper.slim-view
{
    padding: 0;
}
/*------------------------------------------------------------------------------------------------*/
#ysw_fa_wrapper.slim-view .ysw_fa_start_view .ysw_fa_steps_container
{
    padding: 10px 0;
}

@media screen and (max-width:800px) {
	.breadcrumbs {
		display: none;
	}
	#ysw_fa_wrapper .ysw_filter_step_1.step {
		display: none
	}

	#ysw_fa_wrapper .ysw_filter_step_1.step.ysw_filter_step_1-mob {
		display: block
	}

	#ysw_fa_wrapper .ysw_fa_start_view .ysw_fa_submit_container {
		margin: 0 auto 10px;
		text-align: left;
		max-width: 100%;
		padding: 0 20px
	}

	#ysw_fa_wrapper .ysw_fa_start_view .ysw_fa_steps_container .ysw_filter_step_1.step div #ysw_yamaha {
		display: inline-block
	}

	#ysw_fa_wrapper .ysw_fa_start_view .ysw_fa_steps_container .step {
		width: 100%;
		float: none;
		max-width: 100%;
		margin: 0 auto;
		padding-bottom: 0px;
	}

	#ysw_fa_wrapper .ysw_fa_start_view .ysw_fa_steps_container .step.ysw_filter_step_2 {
		margin: 0 auto;
	}

	#ysw_fa_wrapper, .homefiltersection #ysw_fa_wrapper {
		background-image: none;
		padding-top: 20px;
		padding-bottom: 20px;
		width: 100% !important;
	}

	.homefiltersection #ysw_fa_wrapper {
		padding-bottom: 0px;
	}

	#ysw_fa_wrapper .ysw_fa_start_view .ysw_fa_steps_container {
		padding: 10px 0 0 0;
	}

	#ysw_fa_wrapper.slim-view .ysw_fa_start_view .ysw_fa_heading {
		padding-bottom: 0;
	}

	#ysw_fa_wrapper.slim-view .ysw_fa_start_view .ysw_fa_steps_container {
		float: none;
		width: 100%;
		padding: 10px 0;
	}

	#ysw_fa_wrapper.slim-view .ysw_fa_start_view .ysw_fa_submit_container {
		width: 100%;
		float: none;
		padding: 0;
		display: none;
	}

	#ysw_fa_wrapper.slim-view .ysw_fa_start_view .ysw_fa_submit_container button {
		font-size: 24px;
		display: inline-block;
		width: auto;
		height: auto;
		padding: 15px 50px;

		span {
			display: inline;
		}
	}
}

@media screen and (max-width:600px) {

	#ysw_fa_wrapper.slim-view {
		padding-right:10px;
		padding-left:10px;
	}

	#ysw_fa_wrapper .ysw_fa_start_view .ysw_fa_heading {
		font-size:22px;
	}

	#ysw_fa_wrapper .ysw_fa_start_view .ysw_fa_steps_container .step select {
		height:40px;
		font-size:18px;
	}

	#ysw_fa_wrapper .ysw_fa_start_view .ysw_fa_steps_container .step .step-title {
		font-size:18px;
	}

	#ysw_fa_wrapper .ysw_fa_start_view .ysw_fa_submit_container button,
	#ysw_fa_wrapper.slim-view .ysw_fa_start_view .ysw_fa_submit_container button {
		font-size:18px;
		line-height:1.2;
		padding:10px 0;
		width:100%;

		span {
			display:none;
		}

	}

	#ysw_fa_wrapper.slim-view {
		padding-top:0px;
		padding-bottom:0px;
	}

	#ysw_fa_wrapper .ysw_fa_locked_view .ysw_fa_selection {
		font-size:22px;
	}

	#ysw_fa_wrapper .ysw_fa_error_message .ysw_fa_message {
		font-size:22px;
	}

}

.make-modal-product-container{

	display: none;
	background-color:#e5f0fc;
	padding: 15px;
	text-align: left;
    border: 1px solid #f0f0f0;
	margin-bottom: 10px;
	clear: both;

	.mfp-icon{
	    margin-right: 5px;

		i{
	    	font-size: 40px;		
		}
	}

	.success{
		display: none;
   		align-items:center;

		i{
			color: $color-success;		 
		}
	}

	.warning{
		display: none;
   		align-items:center;

		i{
			color: $color-warning;
		}
	}

	.show-options-btn{
		margin-top: 10px;
	}

	&.item-fitting{
		display: block;
		.success{
			display: flex;
		}
	}

	&.item-not-fitting{
		display: block;
		.warning{
			display: flex;
		}
	}

	.your-choice{
		margin: 10px 0px;

		span{
			font-weight: 600;
		}
	}

	.make, .model, .year{
		display: inline-block;	
	}
}

@media screen and (max-width:600px) {
	.make-modal-product-container{
		.make, .model, .year{
			display: block;	
		}		
	}
}
@media screen and (max-width:480px) {
#ysw_fa_wrapper .ysw_fa_start_view .ysw_fa_steps_container .ysw_filter_step_1.step div #ysw_yamaha {
    display: inline-block !important;
}
}
