/* FOOTER */


.footer {
    font-family: "Roboto", Arial, Helvetica, sans-serif;

    .container:after {
        content:'';
        clear:both;
        display:block;
    }

    .newsletter-bar {
        background-color: #f2f1f2;
        padding: 15px 0;

        .cta {
            width:30%;
            float:left;
            font-size:24px;
            line-height:1;
            padding:6px 0;
            font-weight: 600;
            text-transform: uppercase;
        }

        .newsletter-outer {
            width:70%;
            float:left;

            &:after {
                content:'';
                clear:both;
                display:block;
            }

            input[type="text"] {
                float:left;
                border:1px solid #d8d8d8;
                height:38px;
                font-size:16px;
                width:36%;
                padding:0 10px;
                margin-right:1%;
            }

            button[type="submit"] {
                float:left;
                background-color:#008000;
                color:#ffffff;
                text-transform: uppercase;
                font-weight:600;
                font-size:22px;
                line-height:1;
                padding:8px 0;
                width:25%;
                border:0;
                text-shadow: 1px 1px 1px #000000;

                &:hover {
                    background-color:#86983a;
                }

            }
        }
    }

    .bottom-footer {
        background-color: #0266C8;
        color: #ffffff;
        min-height: 200px;
        line-height:1;
        font-weight:600;
        padding:25px 0;
        font-size:18px;

        .foot-columns {

            margin-bottom: 50px;

            &:after {
                content:'';
                display:block;
                clear:both;
            }

            a {
                color:#ffffff;
                text-decoration:none;

                &:hover {
                    color:#fff6aa;
                }
            }

            ul {
                list-style-type:none;
                margin:0;
                padding:0;

                &:after {
                    content:'';
                    display:block;
                    clear:both;
                }
            }

            .foot-col {
                float:left;

                &:nth-child(1) {
                    width:29%;
                }

                &:nth-child(2) {
                    width:23%;
                }

                &:nth-child(3) {
                    width:28%;
                }

                &:nth-child(4) {
                    width:20%;
                }

                .foot-heading {
                    font-size:22px;
                    text-transform:uppercase;
                    font-weight:600;
                    padding-bottom:35px;
                    color:#fff6aa;
                    text-shadow:1px 1px 1px #000000;
                }

                ul.foot-phone-hours {
                    padding-bottom:20px;

                    li {
                        padding-bottom:10px;
                    }
                }

                .foot-email {
                    padding-bottom:30px;
                }

                ul.social-icons {
                    font-size:34px;
                    margin-bottom:30px;

                    li {
                        float:left;
                        margin-right:20px;

                        &:last-child {
                            margin-right:0;
                        }
                    }
                }

                ul.foot-links {

                    li {
                        padding-bottom:10px;
                    }
                }

                ul.foot-credit-card-icons {
                    width:120px;
                    margin:0 auto 10px;

                    li {
                        width:33%;
                        float:left;
                        padding-bottom:5px;

                        img{
                            width: 54px;
                        }

                        &.paypal-credit{
                            width: 100%;

                            img{
                                width: 172px;
                            }
                        }
                    }
                }

                .foot-copyright {
                    font-size:12px;
                }

            }
        }
    }
}


