    .image-link-block {
        text-align:center;
        width:31%;
        float:left;
        position:relative;
        margin-bottom:3.5%;


        &:after {
            clear:both;
            content:'';
            display:block;
        }

        &:nth-child(3n+2) {
            margin-right:3.5%;
            margin-left:3.5%;
        }

        a {
            text-decoration:none;

            &:hover {

                span.name {
                    color:#fff6aa;
                }

            }
        }

        span {
            display:block;

            &.image {
                position:relative;
                max-width: 418.5px;
                height: 242px;
                overflow: hidden;

                &:before {
                    display:block;
                    background-color:rgba(0, 0, 0, 0.5);
                    width:100%;
                    height:100%;
                    content:'';
                    position:absolute;
                    top:0;
                    left:0;
                }

                &.no-overlay:before {
                    display:none;
                }

            }

            &.name {
                width:100%;
                text-align:center;
                font-family: "Roboto", Arial, Helvetica, sans-serif;
                font-size: 28px;
                text-transform:uppercase;
                font-weight: 600;
                color:#ffffff;
                text-decoration:none;
                line-height:1;
                position: absolute;
                top: calc(50% - 14px);
                left: 0;
                text-shadow: 1px 1px 1px #000000;
            }
        }

    }

}

