    .navPages-action.has-subMenu.is-open svg{
        width: 100%;
        height: 100%;
    }

    .navPages .navPage-subMenu-list,
    .navPage-childList{border: 0; margin: 0;}

    // carousel
    .slick-dots li button{
        height: 15px;
        width: 15px;
    }
    .slick-dots li button:before{
        height: 13px;
        width: 13px;
        left: 0;
        top: 0;
    }

    // home page
    .promo-banner li{flex: 0 1 50%; justify-content: center;}
    .promo-banner li:nth-child(2):after{display: none;}
    .promo-banner li:last-child{display: none;}

    .promo-banner li em,
    .promo-banner li > span{flex: 0 1 auto;}

    .heroCarousel .slick-track{
        background-color: $carousel-textBackground;
    }

    // footer
    .footer-info-col.footer-newsletter-col{
        flex: 1 1 100%;
    }

    // product grid
    .productGrid .product{
        flex: 0 1 #{$numProds--grid-tablet}%;
        max-width: #{$numProds--grid-tablet}%;
    }

    // category page

    .sub-category-boxes .sub-cat-box {
        width:31.33%;
    }

    .accordion .sidebarBlock-heading{padding: 14px;}
    #subcategoryList:not(.is-open){display: none;}
    .accordion .navList{padding: 0 14px 14px;}

    .facetedSearch-toggle-indicator{
        text-align: left;
        width: 100%;
    }

    .facetedSearch-toggle-indicator > .toggleLink-text{
        font-size: 20px;
        width: 100%;
    }

    .facetedSearch-toggle-indicator .toggleLink-text i{
        float: right;
    }

    .page-sidebar .toggleLink.button svg{
        fill: $button--secondary-text;
    }
    .page-sidebar .toggleLink.button:hover svg,
    .page-sidebar .toggleLink.button:active svg{
        fill: $button--secondary-text-hover;
    }
    .facetedSearch-toggle-indicator .toggleLink-text--on svg{
        transform: rotate(180deg);
    }
}

