/* stick atc bar */
#stickyATC {
    position: fixed;
	left:0px;
    top: -100px;
    background: #ffffff;
    min-height: 80px;
	height: 80px;
    width: 100%;
    padding: 4px 8px;
    z-index: 5000;
    transition: top 500ms ease-in-out;
	-webkit-box-shadow: 0px 0px 10px 0px rgba(0,0,0,0.35); 
	-moz-box-shadow: 0px 0px 10px 0px rgba(0,0,0,0.35); 
	box-shadow: 0px 0px 10px 0px rgba(0,0,0,0.35); 


	&.sticky {
		position: fixed;
		width: 100%;
		visibility: visible;
	}

	img {
		float:left;
		/*border:1px solid #000000;*/
		margin-left: 10px;
		margin-right: 10px;
		max-width:72px;
		max-height:72px;
	} 

	button#stickyATC-addToCart {
		float: right;
		font-size:18px;
	}

	.stickyATC-title {
		padding-left:10px;
		display: block;
		font-family: "Roboto Condensed",Arial,Helvetica,sans-serif;
		font-size: 18px;
		/*max-width: 60%;*/
		margin-top: 8px;
		font-weight: 600;
	}	
	.price-section {
		font-size:18px;
		.price {
			display:inline-block;
			padding-left:0px;
			font-size:18px ;

			&.price--rrp {
				display:none;
				padding-left:12px;
				font-size:12px;
			}
		}
	}

	@media screen and (max-width:800px) {
		visibility: visible;
		position: fixed; 
		width: 100%; 
		height: 77px; 
		/*border-top: 1px solid #000000;*/
		bottom: 0px;
		top:auto;
		left: 0px;

		.price-section {
			display:none;
		}
		.stickyATC-title {
			display:none;		
		}

		img  {
			display:none;
		}
		.btn-container {
			/*width:100%;*/
			text-align:center;

			button#stickyATC-addToCart {
				font-size:2em;
				/*padding: 20px;*/
				float: none;
				text-transform:uppercase;
				width:100%;
				max-width:400px;
				margin-top: 10px;
				i.fa.fa-shopping-cart {
					display: inline;
					padding: 0px 10px 0px 0px;
					margin: 0px;
				}				
			}
		}
	}	

	@media screen and (min-width:801px) {
		/*border-bottom: 1px solid #000000;*/
		.btn-container {
			/*height:100%;*/

			button#stickyATC-addToCart {
				text-transform:uppercase;
				position: absolute;
				top: 50%;
				right:16px;
				transform: translateY(-50%);
				i.fa.fa-shopping-cart {
					display: inline;
					padding-right: 10px;
				}				
			}
		}
		&.sticky {
			top: 0px;
			left:0px;
			    //bottom: auto;
		}
	}

}
/* fix for embedded youtube videos */
@media screen and (max-width:880px){
	div#tab-description iframe {
		display: block;
	}
}
