.meet-the-team-accordion {
    position: relative;
    display: flex;
    margin: 20px 0;
    flex-wrap: wrap;

    div.accordion {
        cursor: pointer;
        width: 25%;
        height: auto;
        //   max-height: 290px;
        text-align: left;
        border: none;
        outline: 0;
        transition: .4s;
        margin-bottom: 0;
        float: left;
        display: inline-block;
        flex-wrap: wrap;

        img {
            width: 100%;
        }
    }


    div.panel {
        display: none;
        padding: 0 40px;
        background-color: white;
        //  max-height: 0;
        overflow: hidden;
        transition: 0.4s ease-in-out;
        opacity: 0;
        margin-bottom: 0px;
        //  position: absolute;
        // top: 310px;
        width: 100%;
        background-color: #f2f4e6;
    }

    div.panel {
        opacity: 1;
    }

    div.panel.show {
        z-index: 9999;
        //   min-height:340px;
        display: flex;
        margin: 15px 15px 0 15px;

        img {
            padding-bottom: 40px;
            margin: 40px 0 auto 0;
            max-width: 33.3%;
        }

        .accordion-panel-text {
            padding-left: 30px;
            padding-top: 30px;
            padding-bottom: 30px;

            h2 {
                color: #0967c6;
                font-size: 40pt;
                text-align: left;
                margin: 0;
                line-height: 40pt;
            }

            h4 {
                color: #8b9b0d;
                font-size: 24pt;
                text-align: left;
                margin: 0;
                font-weight: 400;
                line-height: 24pt;
                margin-bottom: 20px;
            }
        }
    }

    div.accordion {
        position: relative;

        .accordion-conteiner {
            width: 100%;
            height: auto;
            margin: auto;
            max-width: 100%;
            padding: inherit;
            overflow: hidden;
            position: relative;
            padding: 15px;

            .img {
                z-index: 1;
                position: relative;
            }

            .accordion-name {
                display: none;
            }
        }
    }

    div.accordion {
        .accordion-conteiner:hover {
            .accordion-name {
                display: block;
                position: absolute;
                z-index: 9999;
                width: calc(100% - 30px);
                height: calc(100% - 30px);
                background-color: rgba(139, 155, 11,.5);
                top: 0;
                color: #fff;
                text-transform: uppercase;
                font-size: 24pt;
                font-weight: bold;
                text-align: center;
                padding-top: 30%;
                top: 15px;
                left: 15px;

                span {
                    font-size: 12pt;
                    display: block;
                    width: 100%;
                }
            }
        }

        .accordion-conteiner.thumb-img {
            .accordion-name {
                display: block;
                position: absolute;
                z-index: 9999;
                width: calc(100% - 30px);
                height: calc(100% - 30px);
                top: 0;
                color: #fff;
                text-transform: uppercase;
                font-size: 24pt;
                font-weight: bold;
                text-align: center;
                padding-top: 30%;
                top: 15px;
                left: 15px;

                span {
                    font-size: 12pt;
                    display: block;
                    width: 100%;
                    display: none;
                }

                span.comming-soon {
                    display: block;
                }
            }
        }

        .accordion-conteiner.thumb-img:hover {
            .accordion-name {
                display: block;
                position: absolute;
                z-index: 9999;
                width: calc(100% - 30px);
                height: calc(100% - 30px);
                background: transparent;
                top: 0;
                color: #fff;
                text-transform: uppercase;
                font-size: 24pt;
                font-weight: bold;
                text-align: center;
                padding-top: 30%;
                top: 15px;
                left: 15px;

                span {
                    font-size: 12pt;
                    display: block;
                    width: 100%;
                    display: block;
                }

                span.comming-soon {
                    display: none;
                }
            }
        }
    }
}

